import pick from 'lodash.pick';
import moment from 'moment';
import Vue from 'vue'; // 引入搜索部门弹出框的组件

import departWindow from './DepartWindow';
import JSelectPosition from '@/components/jeecgbiz/JSelectPosition';
import { ACCESS_TOKEN } from "@/store/mutation-types";
import { getAction, getFileAccessHttpUrl } from '@/api/manage';
import { addUser, editUser, queryUserRole, queryall } from '@/api/api';
import { disabledAuthFilter } from "@/utils/authFilter";
import { duplicateCheck } from '@/api/api';
import JImageUpload from '../../../components/jeecg/JImageUpload';
import FileMangement from "@/extends/FileMangement/fileMangement";
export default {
  name: "UserModal",
  components: {
    FileMangement: FileMangement,
    JImageUpload: JImageUpload,
    departWindow: departWindow,
    JSelectPosition: JSelectPosition
  },
  data: function data() {
    return {
      departDisabled: false,
      //是否是我的部门调用该页面
      roleDisabled: false,
      //是否是角色维护调用该页面
      modalWidth: 800,
      drawerWidth: 700,
      modaltoggleFlag: true,
      confirmDirty: false,
      selectedDepartKeys: [],
      //保存用户选择部门id
      checkedDepartKeys: [],
      checkedDepartNames: [],
      // 保存部门的名称 =>title
      checkedDepartNameString: "",
      // 保存部门的名称 =>title
      resultDepartOptions: [],
      userId: "",
      //保存用户id
      username: "",
      userHeads: [],
      userHead: '',
      disableSubmit: false,
      userDepartModel: {
        userId: '',
        departIdList: []
      },
      // 保存SysUserDepart的用户部门中间表数据需要的对象
      dateFormat: "YYYY-MM-DD",
      validatorRules: {
        username: {
          rules: [{
            required: true,
            message: '请输入用户账号!'
          }, {
            validator: this.validateUsername
          }]
        },
        password: {
          rules: [{
            required: true,
            pattern: /^(?=.*[a-zA-Z])(?=.*\d)(?=.*[~!@#$%^&*()_+`\-={}:";'<>?,./]).{8,}$/,
            message: '密码由8位数字、大小写字母和特殊符号组成!'
          }, {
            validator: this.validateToNextPassword
          }]
        },
        confirmpassword: {
          rules: [{
            required: true,
            message: '请重新输入登录密码!'
          }, {
            validator: this.compareToFirstPassword
          }]
        },
        realname: {
          rules: [{
            required: true,
            message: '请输入用户名称!'
          }]
        },
        phone: {
          rules: [{
            validator: this.validatePhone
          }]
        },
        email: {
          rules: [{
            validator: this.validateEmail
          }]
        },
        roles: {},
        //  sex:{initialValue:((!this.model.sex)?"": (this.model.sex+""))}
        workNo: {
          rules: [{
            required: true,
            message: '请输入工号'
          }, {
            validator: this.validateWorkNo
          }]
        },
        telephone: {
          rules: [{
            pattern: /^0\d{2,3}-[1-9]\d{6,7}$/,
            message: '请输入正确的座机号码'
          }]
        }
      },
      departIdShow: false,
      departIds: [],
      //负责部门id
      title: "操作",
      visible: false,
      model: {},
      roleList: [],
      selectedRole: [],
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      uploadLoading: false,
      confirmLoading: false,
      headers: {},
      form: this.$form.createForm(this),
      picUrl: "",
      url: {
        fileUpload: window._CONFIG['domianURL'] + "/sys/common/upload",
        userWithDepart: "/sys/user/userDepartList",
        // 引入为指定用户查看部门信息需要的url
        userId: "/sys/user/generateUserId",
        // 引入生成添加用户情况下的url
        syncUserByUserName: "/act/process/extActProcess/doSyncUserByUserName",
        //同步用户到工作流
        queryTenantList: '/sys/tenant/queryList'
      },
      identity: "1",
      fileList: [],
      tenantList: [],
      currentTenant: [],
      showHeadMsg: false
    };
  },
  created: function created() {
    var token = Vue.ls.get(ACCESS_TOKEN);
    this.headers = {
      "X-Access-Token": token
    };
    this.initTenantList();
  },
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  methods: {
    isDisabledAuth: function isDisabledAuth(code) {
      return disabledAuthFilter(code);
    },
    initTenantList: function initTenantList() {
      var _this = this;

      getAction(this.url.queryTenantList).then(function (res) {
        if (res.success) {
          _this.tenantList = res.result;
        }
      });
    },
    //窗口最大化切换
    toggleScreen: function toggleScreen() {
      if (this.modaltoggleFlag) {
        this.modalWidth = window.innerWidth;
      } else {
        this.modalWidth = 800;
      }

      this.modaltoggleFlag = !this.modaltoggleFlag;
    },
    initialRoleList: function initialRoleList() {
      var _this2 = this;

      queryall().then(function (res) {
        if (res.success) {
          _this2.roleList = res.result;
        } else {
          console.log(res.message);
        }
      });
    },
    loadUserRoles: function loadUserRoles(userid) {
      var _this3 = this;

      queryUserRole({
        userid: userid
      }).then(function (res) {
        if (res.success) {
          _this3.selectedRole = res.result;
        } else {
          console.log(res.message);
        }
      });
    },
    refresh: function refresh() {
      this.selectedDepartKeys = [];
      this.checkedDepartKeys = [];
      this.checkedDepartNames = [];
      this.checkedDepartNameString = "";
      this.userId = "";
      this.resultDepartOptions = [];
      this.departId = [];
      this.departIdShow = false;
      this.currentTenant = [];
    },
    add: function add() {
      this.picUrl = "";
      this.refresh();
      this.edit({
        activitiSync: '1'
      });
    },
    edit: function edit(record) {
      var _this4 = this;

      this.resetScreenSize(); // 调用此方法,根据屏幕宽度自适应调整抽屉的宽度

      var that = this;
      that.initialRoleList();
      that.checkedDepartNameString = "";
      that.form.resetFields();

      if (record.hasOwnProperty("id")) {
        that.loadUserRoles(record.id);
        setTimeout(function () {
          _this4.fileList = record.avatar;
        }, 5);
      }

      that.userId = record.id;
      that.visible = true;
      that.model = Object.assign({}, record);
      that.username = that.model.username;
      that.$nextTick(function () {
        that.form.setFieldsValue(pick(_this4.model, 'username', 'sex', 'realname', 'email', 'phone', 'activitiSync', 'workNo', 'telephone', 'post'));
      }); //身份为上级显示负责部门，否则不显示

      if (this.model.userIdentity == "2") {
        this.identity = "2";
        this.departIdShow = true;
      } else {
        this.identity = "1";
        this.departIdShow = false;
      } // 调用查询用户对应的部门信息的方法


      that.checkedDepartKeys = [];
      that.loadCheckedDeparts(); //update-begin-author:taoyan date:2020710 for:多租户配置

      if (!record.relTenantIds || record.relTenantIds.length == 0) {
        this.currentTenant = [];
      } else {
        this.currentTenant = record.relTenantIds.split(',').map(Number);
      } //update-end-author:taoyan date:2020710 for:多租户配置

    },
    //
    loadCheckedDeparts: function loadCheckedDeparts() {
      var _this5 = this;

      var that = this;

      if (!that.userId) {
        return;
      }

      getAction(that.url.userWithDepart, {
        userId: that.userId
      }).then(function (res) {
        that.checkedDepartNames = [];

        if (res.success) {
          var depart = [];
          var departId = [];

          for (var i = 0; i < res.result.length; i++) {
            that.checkedDepartNames.push(res.result[i].title);
            _this5.checkedDepartNameString = _this5.checkedDepartNames.join(",");
            that.checkedDepartKeys.push(res.result[i].key); //新增负责部门选择下拉框

            depart.push({
              key: res.result[i].key,
              title: res.result[i].title
            });
            departId.push(res.result[i].key);
          }

          that.resultDepartOptions = depart; //判断部门id是否存在，不存在择直接默认当前所在部门

          if (_this5.model.departIds) {
            _this5.departIds = _this5.model.departIds.split(",");
          } else {
            _this5.departIds = departId;
          }

          that.userDepartModel.departIdList = that.checkedDepartKeys;
        } else {
          console.log(res.message);
        }
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.disableSubmit = false;
      this.selectedRole = [];
      this.userDepartModel = {
        userId: '',
        departIdList: []
      };
      this.checkedDepartNames = [];
      this.checkedDepartNameString = '';
      this.checkedDepartKeys = [];
      this.selectedDepartKeys = [];
      this.resultDepartOptions = [];
      this.departIds = [];
      this.departIdShow = false;
      this.identity = "1";
      this.fileList = [];
    },
    moment: moment,
    handleSubmit: function handleSubmit() {
      var _this6 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;

          if (!values.birthday) {
            values.birthday = '';
          } else {
            values.birthday = values.birthday.format(_this6.dateFormat);
          }

          var formData = Object.assign(_this6.model, values);

          if (that.fileList != '') {
            formData.avatar = that.fileList;
          } else {
            formData.avatar = null;
          } //update-begin-author:taoyan date:2020710 for:多租户配置


          formData.relTenantIds = _this6.currentTenant.length > 0 ? _this6.currentTenant.join(',') : ''; //update-end-author:taoyan date:2020710 for:多租户配置

          formData.selectedroles = _this6.selectedRole.length > 0 ? _this6.selectedRole.join(",") : '';
          formData.selecteddeparts = _this6.userDepartModel.departIdList.length > 0 ? _this6.userDepartModel.departIdList.join(",") : '';
          formData.userIdentity = _this6.identity; //如果是上级择传入departIds,否则为空

          if (_this6.identity === "2") {
            formData.departIds = _this6.departIds.join(",");
          } else {
            formData.departIds = "";
          } // that.addDepartsToUser(that,formData); // 调用根据当前用户添加部门信息的方法


          var obj; //头像  mongondb

          if (that.userHead) formData.avatar = that.userHead;

          if (!_this6.model.id) {
            formData.id = _this6.userId;
            obj = addUser(formData);
          } else {
            obj = editUser(formData);
          }

          obj.then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.checkedDepartNames = [];
            that.userDepartModel.departIdList = {
              userId: '',
              departIdList: []
            };
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    validateToNextPassword: function validateToNextPassword(rule, value, callback) {
      var form = this.form;
      var confirmpassword = form.getFieldValue('confirmpassword');

      if (value && confirmpassword && value !== confirmpassword) {
        callback('两次输入的密码不一样！');
      }

      if (value && this.confirmDirty) {
        form.validateFields(['confirm'], {
          force: true
        });
      }

      callback();
    },
    compareToFirstPassword: function compareToFirstPassword(rule, value, callback) {
      var form = this.form;

      if (value && value !== form.getFieldValue('password')) {
        callback('两次输入的密码不一样！');
      } else {
        callback();
      }
    },
    validatePhone: function validatePhone(rule, value, callback) {
      if (!value) {
        callback();
      } else {
        //update-begin--Author:kangxiaolin  Date:20190826 for：[05] 手机号不支持199号码段--------------------
        if (new RegExp(/^1[3|4|5|7|8|9][0-9]\d{8}$/).test(value)) {
          //update-end--Author:kangxiaolin  Date:20190826 for：[05] 手机号不支持199号码段--------------------
          var params = {
            tableName: 'sys_user',
            fieldName: 'phone',
            fieldVal: value,
            dataId: this.userId
          };
          duplicateCheck(params).then(function (res) {
            if (res.success) {
              callback();
            } else {
              callback("手机号已存在!");
            }
          });
        } else {
          callback("请输入正确格式的手机号码!");
        }
      }
    },
    validateEmail: function validateEmail(rule, value, callback) {
      if (!value) {
        callback();
      } else {
        if (new RegExp(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/).test(value)) {
          var params = {
            tableName: 'sys_user',
            fieldName: 'email',
            fieldVal: value,
            dataId: this.userId
          };
          duplicateCheck(params).then(function (res) {
            console.log(res);

            if (res.success) {
              callback();
            } else {
              callback("邮箱已存在!");
            }
          });
        } else {
          callback("请输入正确格式的邮箱!");
        }
      }
    },
    validateUsername: function validateUsername(rule, value, callback) {
      this.username = value;
      var params = {
        tableName: 'sys_user',
        fieldName: 'username',
        fieldVal: value,
        dataId: this.userId
      };
      duplicateCheck(params).then(function (res) {
        if (res.success) {
          callback();
        } else {
          callback("用户名已存在!");
        }
      });
    },
    validateWorkNo: function validateWorkNo(rule, value, callback) {
      var params = {
        tableName: 'sys_user',
        fieldName: 'work_no',
        fieldVal: value,
        dataId: this.userId
      };
      duplicateCheck(params).then(function (res) {
        if (res.success) {
          callback();
        } else {
          callback("工号已存在!");
        }
      });
    },
    handleConfirmBlur: function handleConfirmBlur(e) {
      var value = e.target.value;
      this.confirmDirty = this.confirmDirty || !!value;
    },
    normFile: function normFile(e) {
      console.log('Upload event:', e);

      if (Array.isArray(e)) {
        return e;
      }

      return e && e.fileList;
    },
    beforeUpload: function beforeUpload(file) {
      var fileType = file.type;

      if (fileType.indexOf('image') < 0) {
        this.$message.warning('请上传图片');
        return false;
      } //TODO 验证文件大小

    },
    handleChange: function handleChange(info) {
      this.picUrl = "";

      if (info.file.status === 'uploading') {
        this.uploadLoading = true;
        return;
      }

      if (info.file.status === 'done') {
        var response = info.file.response;
        this.uploadLoading = false;
        console.log(response);

        if (response.success) {
          this.model.avatar = response.message;
          this.picUrl = "Has no pic url yet";
        } else {
          this.$message.warning(response.message);
        }
      }
    },
    // 搜索用户对应的部门API
    onSearch: function onSearch() {
      this.$refs.departWindow.add(this.checkedDepartKeys, this.userId);
    },
    // 获取用户对应部门弹出框提交给返回的数据
    modalFormOk: function modalFormOk(formData) {
      this.checkedDepartNames = [];
      this.selectedDepartKeys = [];
      this.checkedDepartNameString = '';
      this.userId = formData.userId;
      this.userDepartModel.userId = formData.userId;
      this.departIds = [];
      this.resultDepartOptions = [];
      var depart = [];

      for (var i = 0; i < formData.departIdList.length; i++) {
        this.selectedDepartKeys.push(formData.departIdList[i].key);
        this.checkedDepartNames.push(formData.departIdList[i].title);
        this.checkedDepartNameString = this.checkedDepartNames.join(","); //新增部门选择，如果上面部门选择后不为空直接付给负责部门

        depart.push({
          key: formData.departIdList[i].key,
          title: formData.departIdList[i].title
        });
        this.departIds.push(formData.departIdList[i].key);
      }

      this.resultDepartOptions = depart;
      this.userDepartModel.departIdList = this.selectedDepartKeys;
      this.checkedDepartKeys = this.selectedDepartKeys; //更新当前的选择keys
    },
    // 根据屏幕变化,设置抽屉尺寸
    resetScreenSize: function resetScreenSize() {
      var screenWidth = document.body.clientWidth;

      if (screenWidth < 500) {
        this.drawerWidth = screenWidth;
      } else {
        this.drawerWidth = 700;
      }
    },
    identityChange: function identityChange(e) {
      if (e.target.value === "1") {
        this.departIdShow = false;
      } else {
        this.departIdShow = true;
      }
    },
    afterIntFiles: function afterIntFiles(files, status) {
      console.log('afterIntFiles', files);
      this.showHeadMsg = true;

      if (files && files.length) {
        this.userHeads = files;
        this.userHead = files[0].id;
      } else {
        this.userHead = '';
        this.userHeads = [];
      }
    },
    getHeadsHttpUrl: function getHeadsHttpUrl(fileId) {
      return getFileAccessHttpUrl(fileId);
    }
  }
};
