import moment from 'moment';
import { FormTypes } from '@/utils/JEditableTableUtil';
import { randomUUID, randomNumber } from '@/utils/util';
import JEditableTable from '@/components/jeecg/JEditableTable';
export default {
  name: 'DefaultTable',
  components: {
    JEditableTable: JEditableTable
  },
  data: function data() {
    return {
      loading: false,
      columns: [{
        title: '字段名称',
        key: 'dbFieldName',
        // width: '19%',
        width: '300px',
        type: FormTypes.input,
        defaultValue: '',
        placeholder: '请输入${title}',
        validateRules: [{
          required: true,
          // 必填
          message: '请输入${title}' // 显示的文本

        }, {
          pattern: /^[a-z|A-Z][a-z|A-Z\d_-]{0,}$/,
          // 正则
          message: '${title}必须以字母开头，可包含数字、下划线、横杠'
        }, {
          unique: true,
          message: '${title}不能重复'
        }, {
          handler: function handler(type, value, row, column, callback, target) {
            // type 触发校验的类型（input、change、blur）
            // value 当前校验的值
            // callback(flag, message) 方法必须执行且只能执行一次
            //          flag = 是否通过了校验，不填写或者填写 null 代表不进行任何操作
            //          message = 提示的类型，默认使用配置的 message
            // target 行编辑的实例对象
            if (type === 'blur') {
              if (value === 'abc') {
                callback(false, '${title}不能是abc'); // false = 未通过校验
              } else {
                callback(true); // true = 通过验证
              }
            } else {
              callback(true); // 不填写或者填写 null 代表不进行任何操作
            }
          },
          message: '${title}默认提示'
        }]
      }, {
        title: '文件域',
        key: 'upload',
        type: FormTypes.upload,
        // width: '19%',
        width: '300px',
        placeholder: '点击上传',
        token: true,
        responseName: 'message',
        action: window._CONFIG['domianURL'] + '/sys/common/upload'
      }, {
        title: '字段类型',
        key: 'dbFieldType',
        // width: '18%',
        width: '300px',
        type: FormTypes.select,
        options: [// 下拉选项
        {
          title: 'String',
          value: 'string'
        }, {
          title: 'Integer',
          value: 'int'
        }, {
          title: 'Double',
          value: 'double'
        }, {
          title: 'Boolean',
          value: 'boolean'
        }],
        allowInput: true,
        defaultValue: '',
        placeholder: '请选择${title}',
        validateRules: [{
          required: true,
          message: '请选择${title}'
        }]
      }, {
        title: '性别（字典）',
        key: 'sex_dict',
        width: '300px',
        type: FormTypes.select,
        options: [],
        dictCode: 'sex',
        placeholder: '请选择${title}',
        validateRules: [{
          required: true,
          message: '请选择${title}'
        }]
      }, {
        title: '多选测试',
        key: 'multipleSelect',
        // width: '18%',
        width: '300px',
        type: FormTypes.select,
        props: {
          'mode': 'multiple'
        },
        // 支持多选
        options: [{
          title: 'String',
          value: 'string'
        }, {
          title: 'Integer',
          value: 'int'
        }, {
          title: 'Double',
          value: 'double'
        }, {
          title: 'Boolean',
          value: 'boolean'
        }],
        defaultValue: ['int', 'boolean'],
        // 多个默认项
        // defaultValue: 'string,double,int', // 也可使用这种方式
        placeholder: '这里可以多选',
        validateRules: [{
          required: true,
          message: '请选择${title}'
        }]
      }, {
        title: '字段长度',
        key: 'dbLength',
        // width: '8%',
        width: '100px',
        type: FormTypes.inputNumber,
        defaultValue: 32,
        placeholder: '${title}',
        // 是否是统计列，只有 inputNumber 才能设置统计列
        statistics: true,
        validateRules: [{
          required: true,
          message: '请输入${title}'
        }]
      }, {
        title: '日期',
        key: 'datetime',
        // width: '22%',
        width: '320px',
        type: FormTypes.datetime,
        defaultValue: '2019-4-30 14:52:22',
        placeholder: '请选择${title}',
        validateRules: [{
          required: true,
          message: '请选择${title}'
        }]
      }, {
        title: '数字',
        key: 'money',
        width: '320px',
        type: FormTypes.inputNumber,
        defaultValue: '100.32',
        placeholder: '请选择${title}',
        validateRules: [{
          required: true,
          message: '请选择${title}'
        }]
      }, {
        title: '可以为空',
        key: 'isNull',
        // width: '8%',
        width: '100px',
        type: FormTypes.checkbox,
        customValue: ['Y', 'N'],
        // true ,false
        defaultChecked: false
      }, {
        type: FormTypes.popup,
        key: 'popup',
        title: 'JPopup',
        width: '180px',
        popupCode: 'demo',
        field: 'name',
        orgFields: 'name',
        destFields: 'name'
      }, {
        title: '操作',
        key: 'action',
        // width: '8%',
        width: '100px',
        type: FormTypes.slot,
        slotName: 'action'
      }],
      dataSource: [],
      selectedRowIds: []
    };
  },
  mounted: function mounted() {
    this.randomData(23, false);
  },
  methods: {
    /** 表单验证 */
    handleTableCheck: function handleTableCheck() {
      var _this = this;

      this.$refs.editableTable.getValues(function (error) {
        if (error === 0) {
          _this.$message.success('验证通过');
        } else {
          _this.$message.error('验证未通过');
        }
      });
    },

    /** 获取值，忽略表单验证 */
    handleTableGet: function handleTableGet() {
      this.$refs.editableTable.getValues(function (error, values) {
        console.log('values:', values);
      }, false);
      console.log('deleteIds:', this.$refs.editableTable.getDeleteIds());
      this.$message.info('获取值成功，请看控制台输出');
    },

    /** 模拟加载1000条数据 */
    handleTableSet: function handleTableSet() {
      this.randomData(1000, true);
    },
    handleSelectRowChange: function handleSelectRowChange(selectedRowIds) {
      this.selectedRowIds = selectedRowIds;
    },

    /* 随机生成数据 */
    randomData: function randomData(size) {
      var _this2 = this;

      var loading = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : false;

      if (loading) {
        this.loading = true;
      }

      var randomDatetime = function randomDatetime() {
        var time = parseInt(randomNumber(1000, 9999999999999));
        return moment(new Date(time)).format('YYYY-MM-DD HH:mm:ss');
      };

      var begin = Date.now();
      var values = [];

      for (var i = 0; i < size; i++) {
        values.push({
          id: randomUUID(),
          dbFieldName: "name_".concat(i + 1),
          // dbFieldTxt: randomString(10),
          multipleSelect: ['string', ['int', 'double', 'boolean'][randomNumber(0, 2)]],
          dbFieldType: ['string', 'int', 'double', 'boolean'][randomNumber(0, 3)],
          dbLength: randomNumber(0, 233),
          datetime: randomDatetime(),
          isNull: ['Y', 'N'][randomNumber(0, 1)]
        });
      }

      this.dataSource = values;
      var end = Date.now();
      var diff = end - begin;

      if (loading && diff < size) {
        setTimeout(function () {
          _this2.loading = false;
        }, size - diff);
      }
    },
    handleDelete: function handleDelete(props) {
      var rowId = props.rowId,
          target = props.target;
      target.removeRows(rowId);
    }
  }
};
