import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import Vue from 'vue';
import { ACCESS_TOKEN } from "@/store/mutation-types";
import JImageUpload from '../../../../components/jeecg/JImageUpload';
export default {
  name: "JeecgOrderCustomerModal",
  components: {
    JImageUpload: JImageUpload
  },
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      // 表头
      columns: [{
        title: '客户名',
        align: "center",
        dataIndex: 'name'
      }, {
        title: '性别',
        align: "center",
        dataIndex: 'sex'
      }, {
        title: '身份证号码',
        align: "center",
        dataIndex: 'idcard'
      }, {
        title: '身份证扫描件',
        align: "center",
        dataIndex: 'idcardPic'
      }, {
        title: '电话',
        dataIndex: 'telphone',
        align: "center"
      }, {
        title: '订单号码',
        dataIndex: 'orderId',
        align: "center"
      }, {
        title: '创建人',
        dataIndex: 'createBy',
        align: "center"
      }, {
        title: '创建时间',
        dataIndex: 'createTime',
        align: "center"
      }, {
        title: '更新时间',
        dataIndex: 'updateBy',
        align: "center"
      }, {
        title: '更新人',
        dataIndex: 'updateTime',
        align: "center"
      }],
      fileList: [],
      disableSubmit: false,
      selectedRowKeys: [],
      orderId: "",
      hiding: false,
      headers: {},
      picUrl: "",
      picArray: [],
      previewVisible: false,
      previewImage: '',
      addStatus: false,
      editStatus: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      url: {
        add: "/test/order/addCustomer",
        edit: "/test/order/editCustomer",
        fileUpload: window._CONFIG['domianURL'] + "/sys/common/upload",
        getOrderCustomerList: "/test/order/listOrderCustomerByMainId"
      },
      validatorRules: {
        telphone: {
          rules: [{
            validator: this.validateMobile
          }]
        },
        idcard: {
          rules: [{
            validator: this.validateIdCard
          }]
        }
      }
    };
  },
  computed: {
    uploadAction: function uploadAction() {
      return this.url.fileUpload;
    }
  },
  created: function created() {
    var token = Vue.ls.get(ACCESS_TOKEN);
    this.headers = {
      "X-Access-Token": token
    };
  },
  methods: {
    add: function add(orderId) {
      this.hiding = true;

      if (orderId) {
        this.orderId = orderId;
        this.edit({
          orderId: orderId
        }, '');
      } else {
        this.$message.warning("请选择一个客户信息");
      }
    },
    detail: function detail(record) {
      this.edit(record, 'd');
    },
    edit: function edit(record, v) {
      var _this = this;

      if (v == 'e') {
        this.hiding = false;
        this.disableSubmit = false;
      } else if (v == 'd') {
        this.hiding = false;
        this.disableSubmit = true;
      } else {
        this.hiding = true;
        this.disableSubmit = false;
      }

      this.form.resetFields();
      this.orderId = record.orderId;
      this.model = Object.assign({}, record);

      if (record.id) {
        this.hiding = false;
        this.addStatus = false;
        this.editStatus = true;
        this.$nextTick(function () {
          _this.form.setFieldsValue(pick(_this.model, 'id', 'name', 'sex', 'idcard', 'telphone', 'orderId', 'createBy', 'createTime', 'updateBy', 'updateTime'));
        });
        setTimeout(function () {
          _this.fileList = record.idcardPic;
        }, 5);
      } else {
        this.addStatus = false;
        this.editStatus = true;
      }

      this.visible = true;
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.picUrl = "";
      this.fileList = [];
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values);
          console.log(formData);
          formData.orderId = _this2.orderId;

          if (_this2.fileList != '') {
            formData.idcardPic = _this2.fileList;
          } else {
            formData.idcardPic = '';
          }

          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    validateMobile: function validateMobile(rule, value, callback) {
      if (!value || new RegExp(/^1([38][0-9]|4[579]|5[0-3,5-9]|6[6]|7[0135678]|9[89])\d{8}$/).test(value)) {
        callback();
      } else {
        callback("您的手机号码格式不正确!");
      }
    },
    validateIdCard: function validateIdCard(rule, value, callback) {
      if (!value || new RegExp(/(^\d{15}$)|(^\d{18}$)|(^\d{17}(\d|X|x)$)/).test(value)) {
        callback();
      } else {
        callback("您的身份证号码格式不正确!");
      }
    }
  }
};
