import { axios } from '@/utils/request';
import { ACCESS_TOKEN } from "@/store/mutation-types";
import Vue from 'vue';
export default {
  name: 'FlowTest',
  data: function data() {
    return {
      url: "",
      paramJson: "",
      resultJson: {},
      requestMethod: "POST"
    };
  },
  methods: {
    onSearch: function onSearch(value) {
      var _this = this;

      var that = this;

      if (!value) {
        that.$message.error("请填写路径");
        return false;
      }

      this.resultJson = {};
      axios({
        url: value,
        method: this.requestMethod,
        data: this.paramJson
      }).then(function (res) {
        console.log(res);
        _this.resultJson = res;
      }).catch(function (err) {
        that.$message.error("请求异常：" + err);
      });
    },
    changeVal: function changeVal(e) {
      try {
        var json = e.target.value;

        if (json.indexOf(",}") > 0) {
          json = json.replace(",}", "}");
        }

        this.paramJson = JSON.parse(json);
      } catch (e) {
        console.log(e);
        this.$message.error("非法的JSON字符串");
      }
    },
    handleChange: function handleChange(value) {
      this.requestMethod = value;
    },
    created: function created() {
      var token = Vue.ls.get(ACCESS_TOKEN);
      this.headers = {
        "X-Access-Token": token
      };
    }
  }
};
