import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import { duplicateCheck } from '@/api/api';
export default {
  name: "SysDepartRoleModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        roleName: {
          rules: [{
            required: true,
            message: '请输入部门角色名称!'
          }, {
            min: 2,
            max: 30,
            message: '长度在 2 到 30 个字符',
            trigger: 'blur'
          }]
        },
        roleCode: {
          rules: [{
            required: true,
            message: '请输入部门角色编码!'
          }, {
            min: 0,
            max: 64,
            message: '长度不超过 64 个字符',
            trigger: 'blur'
          }, {
            validator: this.validateRoleCode
          }]
        },
        description: {
          rules: [{
            min: 0,
            max: 126,
            message: '长度不超过 126 个字符',
            trigger: 'blur'
          }]
        }
      },
      url: {
        add: "/sys/sysDepartRole/add",
        edit: "/sys/sysDepartRole/edit"
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add(departId) {
      this.edit({}, departId);
    },
    edit: function edit(record, departId) {
      var _this = this;

      this.departId = departId;
      this.form.resetFields();
      this.model = Object.assign({}, record);
      this.visible = true;
      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'roleName', 'roleCode', 'description'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; // 触发表单验证

      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values);
          formData.departId = _this2.departId;
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    validateRoleCode: function validateRoleCode(rule, value, callback) {
      if (/[\u4E00-\u9FA5]/g.test(value)) {
        callback("部门角色编码不可输入汉字!");
      } else {
        var params = {
          tableName: "sys_depart_role",
          fieldName: "role_code",
          fieldVal: value,
          dataId: this.model.id
        };
        duplicateCheck(params).then(function (res) {
          if (res.success) {
            callback();
          } else {
            callback(res.message);
          }
        });
      }
    }
  }
};
