import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import { getAction } from '@/api/manage';
import JDate from '@/components/jeecg/JDate';
import JEditor from '@/components/jeecg/JEditor';
import SelectUserListModal from "./SelectUserListModal";
import moment from 'moment';
export default {
  components: {
    JEditor: JEditor,
    JDate: JDate,
    SelectUserListModal: SelectUserListModal
  },
  name: "SysAnnouncementModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      disableSubmit: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 6
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 18
        }
      },
      labelColX1: {
        xs: {
          span: 24
        },
        sm: {
          span: 3
        }
      },
      wrapperColX1: {
        xs: {
          span: 24
        },
        sm: {
          span: 21
        }
      },
      confirmLoading: false,
      form: this.$form.createForm(this),
      validatorRules: {
        title: {
          rules: [{
            required: true,
            message: '请输入标题!'
          }]
        },
        msgCategory: {
          rules: [{
            required: true,
            message: '请选择消息类型!'
          }]
        },
        msgType: {
          rules: [{
            required: true,
            message: '请选择通告对象类型!'
          }]
        },
        endTime: {
          rules: [{
            required: true,
            message: '请选择结束时间!'
          }, {
            validator: this.endTimeValidate
          }]
        },
        startTime: {
          rules: [{
            required: true,
            message: '请选择开始时间!'
          }, {
            validator: this.startTimeValidate
          }]
        },
        msgAbstract: {
          rules: [{
            required: true,
            message: '请输入摘要!'
          }]
        }
      },
      url: {
        queryByIds: "/sys/user/queryByIds",
        add: "/sys/annountCement/add",
        edit: "/sys/annountCement/edit"
      },
      userType: false,
      userIds: [],
      selectedUser: [],
      disabled: false,
      msgContent: "",
      userList: []
    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      this.form.resetFields();
      this.model = {};
      this.disable = false;
      this.visible = true;
      this.getUser(record);
    },
    getUser: function getUser(record) {
      var _this = this;

      this.model = Object.assign({}, record); // 指定用户

      if (record && record.msgType === "USER") {
        this.userType = true;
        this.userIds = record.userIds;
        getAction(this.url.queryByIds, {
          userIds: this.userIds
        }).then(function (res) {
          if (res.success) {
            //update--begin--autor:wangshuai-----date:20200601------for：系统公告选人后，不能删除------
            var userList = [];

            for (var i = 0; i < res.result.length; i++) {
              var user = {};
              user.label = res.result[i].realname;
              user.key = res.result[i].id;
              userList.push(user);
            }

            _this.selectedUser = userList; //update--begin--autor:wangshuai-----date:20200601------for：系统公告选人后，不能删除------

            _this.$refs.UserListModal.edit(res.result, _this.userIds);
          }
        });
      }

      this.$nextTick(function () {
        _this.form.setFieldsValue(pick(_this.model, 'endTime', 'startTime', 'titile', 'msgContent', 'priority', 'msgCategory', 'msgType', 'sendStatus', 'msgAbstract'));
      });
    },
    close: function close() {
      this.$emit('close');
      this.selectedUser = [];
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this2 = this;

      var that = this; //当设置指定用户类型，但用户为空时，后台报错

      if (this.userType && !(this.userIds != null && this.userIds.length > 0)) {
        this.$message.warning('指定用户不能为空！');
        return;
      } // 触发表单验证


      this.form.validateFields(function (err, values) {
        if (!err) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this2.model.id) {
            httpurl += _this2.url.add;
            method = 'post';
          } else {
            httpurl += _this2.url.edit;
            method = 'put';
          }

          var formData = Object.assign(_this2.model, values);

          if (_this2.userType) {
            formData.userIds = _this2.userIds;
          }

          console.log(formData);
          httpAction(httpurl, formData, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
              that.resetUser();
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        }
      });
    },
    handleCancel: function handleCancel() {
      this.visible = false;
      this.$emit('close');
      this.resetUser();
    },
    resetUser: function resetUser() {
      this.userType = false;
      this.userIds = [];
      this.selectedUser = [];
      this.disabled = false;
      this.$refs.UserListModal.edit(null, null);
    },
    selectUserIds: function selectUserIds() {
      this.$refs.UserListModal.add(this.selectedUser, this.userIds);
    },
    chooseMsgType: function chooseMsgType(value) {
      if ("USER" == value) {
        this.userType = true;
      } else {
        this.userType = false;
        this.selectedUser = [];
        this.userIds = [];
      }
    },
    // 子modal回调
    choseUser: function choseUser(userList) {
      this.selectedUser = [];
      this.userIds = [];

      for (var i = 0; i < userList.length; i++) {
        //update--begin--autor:wangshuai-----date:20200601------for：系统公告选人后，不能删除------
        var user = {};
        user.label = userList[i].realname;
        user.key = userList[i].id;
        this.selectedUser.push(user); //update--end--autor:wangshuai-----date:20200601------for：系统公告选人后，不能删除------

        this.userIds += userList[i].id + ",";
      }
    },
    startTimeValidate: function startTimeValidate(rule, value, callback) {
      var endTime = this.form.getFieldValue("endTime");

      if (!value || !endTime) {
        callback();
      } else if (moment(value).isBefore(endTime)) {
        callback();
      } else {
        callback("开始时间需小于结束时间");
      }
    },
    endTimeValidate: function endTimeValidate(rule, value, callback) {
      var startTime = this.form.getFieldValue("startTime");

      if (!value || !startTime) {
        callback();
      } else if (moment(startTime).isBefore(value)) {
        callback();
      } else {
        callback("结束时间需大于开始时间");
      }
    },
    handleChange: function handleChange(userList) {
      if (userList) {
        this.userIds = [];
        var users = [];

        for (var i = 0; i < userList.length; i++) {
          var user = {};
          user.id = userList[i].key;
          user.realname = userList[i].label;
          this.userIds += userList[i].key + ',';
          users.push(user);
        }
      }

      this.$refs.UserListModal.edit(users, this.userIds);
    }
  }
};
