import { putAction, deleteAction, getFileAccessHttpUrl } from "@/api/manage"; // 高度封装的请求，请务必使用 superRequest.call(this,{}) 的方式调用

function superRequest(options) {
  var _this = this;

  this.loading = !!options.loading;
  options.promise.then(function (res) {
    if (res.success && typeof options.success === 'function') {
      options.success(res);
    } else {
      throw new Error(res.message);
    }
  }).catch(function (e) {
    console.error('查询已删除的用户失败：', e);

    _this.$message.warning('查询已删除的用户失败：' + (e.message || e));
  }).finally(function () {
    _this.loading = false;
  });
}

