import {postAction} from '@/api/manage'

export default {
  name: "Step2",
  props: ['userList'],
  data() {
    return {
      form: this.$form.createForm(this),
      loading: false,
      // accountName: this.userList.username,
      dropList: "0",
      captcha: "",
      show: true,
      state: {
        time: 60,
        smsSendBtn: false,
      },
      formLogin: {
        captcha: "",
        mobile: "",
      },
      validatorRules: {
        captcha: {rule: [{required: true, message: '请输入短信验证码!'}, {validator: this.validateCaptcha}]},
        phone: {rule: [{required: true, message: '请输入手机号码!'}, {validator: this.validatePhone}]},
      },
    }
  },
  computed: {
  },
  methods: {
    nextStep() {
      let that = this
      that.loading = true
      this.form.validateFields((err, values) => {
        console.log(values);
        if (!err) {
          if (that.dropList == "0") {
            if (values.captcha == undefined) {
              this.cmsFailed("请输入短信验证码!");
            } else {
              var params = {}
              params.phone = values.phone;
              params.smscode = values.captcha;
              postAction("/sys/user/phoneVerification", params).then((res) => {
                if (res.success) {
                  console.log(res);
                  var userList = {
                    username: res.result.username,
                    phone: values.phone,
                    smscode: res.result.smscode
                  };
                  setTimeout(function () {
                    that.$emit('nextStep', userList)
                  }, 0)
                } else {
                  this.cmsFailed(res.message);
                }
              })

            }
          }


        }
      })
    },
    getCaptcha(e) {
      e.preventDefault();
      let that = this;
      let phone=that.form.getFieldValue("phone")
      if(!phone){
        this.cmsFailed("手机号不能为空!");
        return;
      }
      this.state.smsSendBtn = true;
      let interval = window.setInterval(() => {
        if (that.state.time-- <= 0) {
          that.state.time = 60;
          that.state.smsSendBtn = false;
          window.clearInterval(interval);
        }
      }, 1000);

      const hide = this.$message.loading('验证码发送中..', 0);
      let smsParams = {
        mobile: phone,
        smsmode: "2"
      };
      postAction("/sys/sms", smsParams).then(res => {
        if (!res.success) {
          setTimeout(hide, 1);
          this.cmsFailed(res.message);
        }
        setTimeout(hide, 500);
      })
    },
    cmsFailed(err) {
      this.$notification['error']({
        message: "验证错误",
        description: err,
        duration: 4,
      });
    },
    handleChangeSelect(value) {
      var that = this;
      console.log(value);
      if (value == 0) {
        that.dropList = "0";
        that.show = true;
      } else {
        that.dropList = "1";
        that.show = false;
      }
    },
    validatePhone(rule,value,callback){
        if(value){
          var myreg=/^[1][3,4,5,7,8][0-9]{9}$/;
          if(!myreg.test(value)){
            callback("请输入正确的手机号")
          }else{
            callback();
          }
        }else{
          callback()
        }
    }
  }

}
